import React from 'react'
import { useState, useEffect } from 'react'
import axios from 'axios'

const Dashboard = () => {
    const [title, setTitle] = useState(null)
    const [reps, setReps] = useState(null)
    const [load, setLoad] = useState(null)
    const [error, setError] = useState(null)
    const [pending, setPending] = useState(true)

    useEffect( () => {
        setTimeout( () => {
            axios.get('https://workoutapi-fjcr.onrender.com/api/workouts')
            .then((data) => {
                const {info} = data;
            })
            .catch((err) => {
                setError(err.message);
                setPending(false);
            })
        }, 500)
    }, [])

    return (
        <div className='dashboard'>
            {pending && <h3>Loading workouts...</h3>}
            {error && <h3>Error incurred...please retry</h3>}
            {!pending && (
                info.map((workout) => (
                    <div className="preview" key={info.id}>
                        <h3>Workout - {info.title}</h3>
                        <h4>Reps - {info.reps}</h4>
                        <h4>Load(kg) - {info.load}</h4>
                    </div>
                ))
            )}
        </div>
    )
}

export default Dashboard;
