import React, { useState } from 'react'
import { Link } from 'react-router-dom'

const Login = () => {
    const [error, setError] = useState(null)
    const [email, setEmail] = useState(null)
    const [password, setPassword] = useState(null)
    const [pending, setPending] = useState(false)
    const [success, setSuccess] = useState(false)
    const handleSubmit = (e) => {
        e.preventDefault();
        setPending(true);
        const profile = { "email": email, "password": password };
        console.log(password, email);
        axios.post('https://workoutapi-fjcr.onrender.com/api/user/login', profile, config)
        .then((res) => {
            setSuccess(true);
            setError(null);
            const { token } = res.data;
            console.log(token);
            localStorage.setItem('email', email);
            localStorage.setItem('token', token);
            setTimeout(() => {
                navigate('/dashboard');
            }, 1000);
        })
        .catch((err) => {
            setPending(false);
            setError(err.message);
            console.log(err.message);
            setSuccess(false);
            setTimeout(() => {
                setError(null);
            }, 1500)
        })
    }

    return (
        <div className='login'>
            <h2>Login In</h2>
            <section style={{ textAlign: 'end' }}>
                <Link to="/">Home</Link>
            </section>
            <form onSubmit={handleSubmit}>
                <label>EmailAddress: </label>
                <div>
                    <input
                        type="email"
                        required
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}
                    />
                </div>
                <label> Password: </label>
                <div>
                    <input
                        type="password"
                        required
                        onChange={(e) => {
                            setPassword(e.target.value)
                        }}
                    />
                </div>
                <div>
                    {!pending && <button type="submit">Log In</button>}
                    {pending && <button disabled>Loging user in...</button>}
                    {error && (
                        <div>
                            <h5>An error occured, please try again !</h5>
                        </div>
                    )
                    }
                    {success && (
                        <div>
                            <h5>Successfully Registered! Redirecting...</h5>
                        </div>
                    )}
                </div>
                {!pending && (
                    <div style={{ alignContent: 'end', paddingTop: '20px' }}>
                        Already Registered?{' '}
                        <Link to="/login">Log In</Link>
                    </div>
                )}
            </form>
        </div>
  )
}

export default Login;
