import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

const Signup = () => {
    const [email, setEmail] = useState(null)
    const [pwd, setPwd] = useState(null)
    const [pending, setPending] = useState(false)
    const [error, setError] = useState(false)
    const navigate = useNavigate();
    const config = {
        headers: {
            'Content-Type': "application/json"
        }
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        setPending(true);
        const profile = {email, pwd};
        axios.post('//https://workoutapi-fjcr.onrender.com/api/user/signup', profile, config)
        .then((res) => {
            if(!res.ok){
                throw Error('Could not reach the server !')
            }
            else{
                const { token } = res.data;
                localStorage.setItem('email', email);
                localStorage.setItem('token', token);
                setTimeout(() => {}, 1000)
            }
        })
        .catch((err) => {
            setPending(false);
            setError(true);
            console.log(err.message);
        })
    }

  return (
    <div className="createuser">
        <h2>New User</h2>
            <form onSubmit={handleSubmit}>
                <label> User EmailAddress: </label>
                <div>
                    <input 
                        type="email"
                        required 
                        value = {email}
                        onChange={(e) => {
                            setEmail(e.target.value)
                            // console.log(e);
                        }}
                    />
                </div>
                <label> Password: </label>
                <div>
                    <input
                        type="password" 
                        required
                        value = {pwd}
                        onChange={(e) => {
                            setPwd(e.target.value)
                        }}
                    ></input>
                </div>
                <div>
                    {!pending && <input type="submit" value="Add User"/>}
                    {pending && <button disabled>Creating User...</button>}
                </div>
            </form>
     </div>
  )
}

export default Signup;
