import React from 'react'
import { useState, useEffect } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import useFetch from './useFetch'

const Dashboard = () => {
    const [title, setTitle] = useState(null)
    const [data, setData] = useState([])
    const [error, setError] = useState(null)
    const [pending, setPending] = useState(true)

    useEffect( () => {
        setTimeout( () => {
            axios.get('https://workoutapi-fjcr.onrender.com/api/workouts', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                  }
            })
            .then((res) => {
                setData(res.data);
                console.log(data);
                setError(null)
                setPending(false)
            })
            .catch((err) => {
                setError(err.message);
                setPending(false);
            })
        }, 250)
    }, [])

    return (
        <div className='dashboard'>
            <h3>Your WorkOut Space:</h3>
            <span style={{alignContent: 'end'}}>
                <Link to="/addworkout">Add WorkOut</Link>
            </span>
            <useFetch />
            {pending && <h3>Loading workouts...</h3>}
            {error && <h3>Error incurred...please retry</h3>}
            {!pending && (
                data.map((workout) => (
                    <div className="preview" key={workout._id}>
                        <h3>Workout - {workout.title}</h3>
                        <h4>Reps - {workout.reps}</h4>
                        <h4>Load(kg) - {workout.load}</h4>
                    </div>
                ))
            )}
        </div>
    )
    return (
        <div className="dashboard">
            <h3>Your WorkOut Space:</h3>
            <span style={{alignContent: 'end'}}>
                <Link to="/addworkout">Add WorkOut</Link>
            </span>
            <useFetch />
        </div>
    )
}

export default Dashboard;
