import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'

const UseFetch = ({pending}) => {
    const [title, setTitle] = useState(null)
    const [data, setData] = useState([])
    const [error, setError] = useState(null)
    const [left, setLeft] = useState(pending)

    useEffect( () => {
        axios.get('https://workoutapi-fjcr.onrender.com/api/workouts', {
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
                }
        })
        .then((res) => {
            setData(res.data);
            console.log(data);
            setError(null)
            setLeft(false)
        })
        .catch((err) => {
            setError(err.message);
            setLeft(false);
        })
    }, [])

    return (
        <div className='fetchboard'>
            {left && <h3>Loading workouts...</h3>}
            {error && <h3>Error incurred...please retry</h3>}
            {!left && (
                data.map((workout) => (
                    <div className="preview" key={workout._id}>
                        <h4>Workout - {workout.title}</h4>
                        <h4>Reps - {workout.reps}</h4>
                        <h4>Load(kg) - {workout.load}</h4>
                    </div>
                ))
            )}
        </div>
    )
}

export default UseFetch;
