import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios'

const Signup = () => {
    const [email, setEmail] = useState(null)
    const [pwd, setPwd] = useState(null)
    const [pending, setPending] = useState(false)
    const [error, setError] = useState(null)
    const [success, setSuccess] = useState(false) 
    const navigate = useNavigate();
    const config = {
        headers: {
            'Content-Type': "application/json"
        }
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        setPending(true);
        const profile = {email, pwd};
        fetch('https://workoutapi-fjcr.onrender.com/api/user/signup', {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(profile)
        })
        // axios.post('https://workoutapi-fjcr.onrender.com/api/user/signup', profile)
        .then((res) => {
            if(!res.ok){
                throw Error('Could not reach the server !')
            }
            else{
                setSuccess(true);
                setError(null);
                const { token } = res.data;
                localStorage.setItem('email', email);
                localStorage.setItem('token', token);
                setTimeout(() => {
                    navigate('/dashboard');
                }, 2000);
            }
        })
        .catch((err) => {
            setPending(false);
            setError(err.message);
            console.log(err.message);
            setSuccess(false);
            setTimeout(() => {
                setError(null);
            }, 1500)
        })
    }

  return (
    <div className="createuser">
        <h2>New User</h2>
            <section style={{textAlign:'end'}}>
                <Link to="/">Home</Link>
            </section>
            <form onSubmit={handleSubmit}>
                <label> User EmailAddress: </label>
                <div>
                    <input 
                        type="email"
                        required 
                        value = {email}
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}
                    />
                </div>
                <label> Password: </label>
                <div>
                    <input
                        type="password" 
                        required
                        value = {pwd}
                        onChange={(e) => {
                            setPwd(e.target.value)
                        }}
                    ></input>
                </div>
                <div>
                    {!pending && <button type="submit">Sign Up</button>}
                    {pending && <button disabled>Creating User...</button>}
                    {error && (
                        <div>
                            <h5>An error occured, please try again !</h5>
                        </div>
                    )
                    }
                    {success && (
                        <div>
                            <h5>Successfully Registered! Redirecting...</h5>
                        </div>
                    )}
                </div>
                {!pending && (
                    <div style={{alignContent:'end', paddingTop:'20px'}}>
                        Already Registered?{' '}
                        <Link to="/login">Log In</Link>
                    </div>
                )}
            </form>
     </div>
  )
}

export default Signup;
