import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios'

const Signup = () => {
    const [email, setEmail] = useState(null)
    const [password, setPassword] = useState(null)
    const [pending, setPending] = useState(false)
    const [error, setError] = useState(null)
    const [success, setSuccess] = useState(false)
    const navigate = useNavigate();
    const config = {
        headers: {
            'Content-Type': "application/json"
        }
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        setPending(true);
        const profile = { "email": email, "password": password };
        console.log(password, email);
        const data = await axios.post('https://workoutapi-fjcr.onrender.com/api/user/signup', profile, config)
        .then(() => {
            setSuccess(true);
            const {token} = data;
            console.log(data);
            localStorage.setItem('email', email);
            localStorage.setItem('token', token);
            setTimeout(() => {
                navigate('/dashboard');
            }, 2000);
        })
        // .then((res) => {
        //     setSuccess(true);
        //     setError(null);
        //     const { token } = res.data;
        //     console.log(token);
        //     localStorage.setItem('email', email);
        //     localStorage.setItem('token', token);
        //     setTimeout(() => {
        //         navigate('/dashboard');
        //     }, 2000);
        // })
        // .catch((error) => {
        //     if (error.response) {
        //       setError('Invalid email or password. Please try again.');
        //     } else if (error.request) {
        //       setError('Network error. Please try again later.');
        //     } else if (error.message) {
        //       setError('An unexpected error occurred. Please try again.');
        //     }
        //     setPending(false);
        //     setTimeout(() => {
        //       setError(null);
        //     }, 1500);
        // })
        // .catch((err) => {
        //     setPending(false);
        //     setError(err.message);
        //     console.log(err.message);
        //     setSuccess(false);
        //     setTimeout(() => {
        //         setError(null);
        //     }, 1500)
        // })
    }

    return (
        <div className="createuser">
            <h2>New User</h2>
            <section style={{ textAlign: 'end' }}>
                <Link to="/">Home</Link>
            </section>
            <form onSubmit={handleSubmit}>
                <label> User EmailAddress: </label>
                <div>
                    <input
                        type="email"
                        required
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}
                    />
                </div>
                <label> Password: </label>
                <div>
                    <input
                        type="password"
                        required
                        onChange={(e) => {
                            setPassword(e.target.value)
                        }}
                    ></input>
                </div>
                <div>
                    {!pending && <button type="submit">Sign Up</button>}
                    {pending && <button disabled>Creating User...</button>}
                    {error && (
                        <div>
                            <h5>An error occured, please try again !</h5>
                        </div>
                    )
                    }
                    {success && (
                        <div>
                            <h5>Successfully Registered! Redirecting...</h5>
                        </div>
                    )}
                </div>
                {!pending && (
                    <div style={{ alignContent: 'end', paddingTop: '20px' }}>
                        Already Registered?{' '}
                        <Link to="/login">Log In</Link>
                    </div>
                )}
            </form>
        </div>
    )
}

export default Signup;
